let currentUnit="km";const kmToMilesFactor=.621371;function updateUnit(){currentUnit=document.getElementById("unitToggle").value;const e=document.getElementsByClassName("unit");for(let t of e)t.textContent=currentUnit;document.getElementById("result").innerText=""}function calculateDistance(){const n=parseFloat(document.getElementById("weekdayDays").value),s=parseFloat(document.getElementById("weekdayDistance").value),o=parseFloat(document.getElementById("weeksOff").value),i=parseFloat(document.getElementById("weekendDistance").value),a=parseFloat(document.getElementById("vacationDistance").value),e=52,r=e-o,c=n*s*r,l=i*e;let t=c+l+a;currentUnit==="miles"&&(t*=kmToMilesFactor),document.getElementById("result").innerText=`Total Annual Distance: ${t.toFixed(2)} ${currentUnit}`}updateUnit()